//go:build ignore

package main

import (
	"fmt"
	"os"

	"github.com/tonkeeper/tongo/tlb/parser"
)

var bitsSizes = []int{80, 96, 128, 256, 264, 320, 352, 512}
var intSizes = []int{128, 256, 257}
var uintSizes = []int{128, 160, 220, 256}

func main() {
	s1 := parser.GenerateVarUintTypes(32)
	s2 := parser.GenerateConstantInts(64)
	s3 := parser.GenerateConstantBigInts(intSizes)
	s4 := parser.GenerateConstantBigUints(uintSizes)
	s5 := parser.GenerateBitsTypes(bitsSizes)
	f, err := os.Create("integers.go")
	if err != nil {
		panic(err)
	}
	_, err = fmt.Fprint(f, `package tlb
// Code autogenerated. DO NOT EDIT. 

import (
	"bytes"
	"encoding/hex"
	"fmt"
	"math/big"
	"strconv"
	"strings"

	"github.com/tonkeeper/tongo/boc"
)
`)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s1)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s2)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s3)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s4)
	if err != nil {
		panic(err)
	}
	_, err = f.WriteString(s5)
	if err != nil {
		panic(err)
	}
}
